function getLocalIPAddress() {
  const networkInterfaces = os.networkInterfaces();

  for (const interfaceName in networkInterfaces) {
    for (const network of networkInterfaces[interfaceName]) {
      if (network.family === 'IPv4' && !network.internal) {
        return network.address; // Yerel IP adresini döner
      }
    }
  }
  return 'localhost'; // Eğer IP adresi bulunamazsa localhost döner
}

app.get('/wp-json/wp/v2/posts', (req, res) => {
  const { page = 1, per_page = 21, categories } = req.query;

  // Sayfa ve kategori filtreleme
  const filteredPosts = examplePosts.filter(post => (categories ? post.categories.includes(parseInt(categories)) : true));

  // Sayfalama
  const startIndex = (page - 1) * per_page;
  const paginatedPosts = filteredPosts.slice(startIndex, startIndex + parseInt(per_page));

  if (paginatedPosts.length === 0) {
    return res.status(400).json({
      code: 'rest_post_invalid_page_number',
      message: 'The page number requested is larger than the number of pages available.',
      data: {
        status: 400,
      },
    });
  }

  setTimeout(() => {
    res.json(paginatedPosts);
  }, 1000);
});

